INSERT INTO `wp_posts` VALUES("39", "1", "2013-02-18 21:29:56", "2013-02-18 21:29:56", "<h4>ALWD Comments to the Standards Review Committee</h4>\r\nDocuments filed by ALWD are available for download as PDF files. <a href=\"#\">Learn more »</a>\r\n<h4>ABA Standards</h4>\r\nOne of the most important bodies in law school accreditation is the Council of the Section of Legal Education and Admissions to the Bar. The United States Department of Education recognizes the Section (along with its Accreditation Committee) as the national accrediting agency for law schools. ABA-accredited law schools meet the legal education requirements of all state bars; only four states do not require graduation from an ABA-accredited law school for bar admission. <a href=\"http://alwd.org/academics/aba_standards_tour1.html\">Learn more »</a>\r\n<h4>LRW Programs (Upper-Level Writing Requirements)</h4>\r\nThis section of the website is currently undergoing construction. You can currently view upper-level writing requirement <a href=\"#\">here</a>.", "Academics", "", "publish", "open", "open", "", "academics", "", "", "2013-02-19 17:13:14", "2013-02-19 17:13:14", "", "0", "http://localhost/alwd.org/?page_id=39", "50", "page", "", "0");
