INSERT INTO `wp_posts` VALUES("247", "1", "2013-02-18 22:31:56", "2013-02-18 22:31:56", "<h4>Thomas F. Blackwell Award: Call for Nominations for 2013 Recipient</h4>\r\nThe 2012-2013 Blackwell Award Committee invites nominations for one of the most prestigious awards in legal writing, the Thomas F. Blackwell Memorial Award for Outstanding Achievement in the Field of Legal Writing. The winner will be announced in early fall 2012, and the award will be presented at the 2013 AALS meeting in New Orleans, Louisiana. A plaque, listing the winners, is on display at Appalachian Law School.\r\n\r\n<a href=\"#\">Learn more »</a>\r\n<h4>2012 Thomas F. Blackwell Memorial Award</h4>\r\nThe Association of Legal Writing Directors and the Legal Writing Institute announced today that <strong>Suzanne Rowe, </strong>Director of the Legal Research and Writing Program at the University of Oregon, received the 2012 Thomas F. Blackwell Award, one of the most prestigious awards in the legal writing field.\r\n\r\n<a href=\"#\">Learn more »</a>\r\n<h4>2012 Legal Writing Scholarship Grants, Sponsored by LWI-ALWD-LexisNexis</h4>\r\nThe Legal Writing Institute (LWI), the Association of Legal Writing Directors (ALWD), and LexisNexis are pleased to announce the Legal Writing Scholarship Grants for teachers of legal research and writing for 2012.  These grants reflect the three organizations\' commitment to the professional development of legal research and writing professionals.\r\n\r\n<a href=\"#\">Learn more »</a>", "Awards", "", "inherit", "open", "open", "", "31-revision-19", "", "", "2013-02-18 22:31:56", "2013-02-18 22:31:56", "", "31", "http://localhost/alwd.org/31-revision-19/", "0", "revision", "", "0");
