INSERT INTO `wp_posts` VALUES("121", "1", "2013-02-19 17:10:11", "2013-02-19 17:10:11", "<h4>ALWD Comments to the Standards Review Committee</h4>\r\nDocuments filed by ALWD are available for download as PDF files.Â <a href=\"#\">Learn more &raquo;</a>\r\n<h4>ABA Standards</h4>\r\nOne of the most important bodies in law school accreditation is the Council of the Section of Legal Education and Admissions to the Bar. The United States Department of Education recognizes the Section (along with its Accreditation Committee) as the national accrediting agency for law schools. ABA-accredited law schools meet the legal education requirements of all state bars; only four states do not require graduation from an ABA-accredited law school for bar admission.Â <a href=\"http://alwd.org/academics/aba_standards_tour1.html\">Learn more&gt;</a>\r\n<h4>LRW Programs (Upper-Level Writing Requirements)</h4>\r\nThis section of the website is currently undergoing construction. You can currently view upper-level writing requirementÂ <a href=\"http://alwd.org/academics/lrw.html\">here.</a>", "Academics", "", "inherit", "open", "open", "", "39-revision-3", "", "", "2013-02-19 17:10:11", "2013-02-19 17:10:11", "", "39", "http://localhost/alwd.org/39-revision-3/", "0", "revision", "", "0");
