b.[/panel]\r\n[/accordion]\r\n<h4>Citation Questions</h4>\r\n[accordion]\r\n[panel title=\"The Title\"]...[/panel]\r\n[panel title=\"The Title\"]...[/panel]\r\n[panel title=\"The Title\"]...[/panel]\r\n[panel title=\"The Title\"]...[/panel]\r\n[panel title=\"The Title\"]...[/panel]\r\n[panel title=\"The Title\"]...[/panel]\r\n[panel title=\"The Title\"]...[/panel]\r\n[panel title=\"The Title\"]...[/panel]\r\n[panel title=\"The Title\"]...[/panel]\r\n[panel title=\"The Title\"]...[/panel]\r\n[panel title=\"The Title\"]...[/panel]\r\n[panel title=\"The Title\"]...[/panel]\r\n[panel title=\"The Title\"]...[/panel]\r\n[/accordion]", "Frequently Asked Questions", "", "inherit", "open", "open", "", "637-revision-32", "", "", "2013-02-26 22:32:15", "2013-02-26 22:32:15", "", "637", "http://localhost/alwd.org/637-revision-32/", "0", "revision", "", "0");
